/**
 * First connect ALL client ports contained in this component, then start all services:
 * activate state, push, etc...
 */
Smart::StatusCode CTest::connectAndStartAllServices() {
	Smart::StatusCode status = Smart::SMART_OK;
	
	status = connectColorQueryServiceReq(connections.colorQueryServiceReq.serverName, connections.colorQueryServiceReq.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectObjectRecognitionQueryServiceReq(connections.objectRecognitionQueryServiceReq.serverName, connections.objectRecognitionQueryServiceReq.serviceName);
	if(status != Smart::SMART_OK) return status;
	return status;
}

/**
 * Start all tasks contained in this component.
 */
void CTest::startAllTasks() {
	// start task Test
	if(connections.test.scheduler != "DEFAULT") {
		ACE_Sched_Params test_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.test.scheduler == "FIFO") {
			test_SchedParams.policy(ACE_SCHED_FIFO);
			test_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.test.scheduler == "RR") {
			test_SchedParams.policy(ACE_SCHED_RR);
			test_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		test->start(test_SchedParams, connections.test.cpuAffinity);
	} else {
		test->start();
	}
}

