void CTest::loadParameter(int argc, char *argv[])
{
	/*
	 Parameters can be specified via command line --filename=<filename> or -f <filename>

	 With this parameter present:
	   - The component will look for the file in the current working directory,
	     a path relative to the current directory or any absolute path
	   - The component will use the default values if the file cannot be found

	 With this parameter absent:
	   - <Name of Component>.ini will be read from current working directory, if found there
	   - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	   - Default values will be used if neither found in working directory or /etc
	 */
	SmartACE::SmartIniParameter parameter;
	std::ifstream parameterfile;
	bool parameterFileFound = false;

	// load parameters
	try
	{
		// if paramfile is given as argument
		if(parameter.tryAddFileFromArgs(argc,argv,"filename", 'f'))
		{
			parameterFileFound = true;
			std::cout << "parameter file is loaded from an argv argument \n";
		} else if(parameter.searchFile("CTest.ini", parameterfile)) {
			parameterFileFound = true;
			std::cout << "load CTest.ini parameter file\n";
			parameter.addFile(parameterfile);
		} else {
			std::cout << "WARNING: CTest.ini parameter file not found! (using default values or command line arguments)\n";
		}
		
		// add command line arguments to allow overwriting of parameters
		// from file
		parameter.addCommandLineArgs(argc,argv,"component");
		
		// initialize the naming service using the command line parameters parsed in the
		// SmartIniParameter class. The naming service parameters are expected to be in
		// the "component" parameter group.
		SmartACE::NAMING::instance()->checkForHelpArg(argc,argv);
		if(parameterFileFound) 
		{
			if(SmartACE::NAMING::instance()->init(parameter.getAllParametersFromGroup("component")) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		} else {
			if(SmartACE::NAMING::instance()->init(argc, argv) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		}
			
		// print all known parameters
		// parameter.print();
		
		//--- server port // client port // other parameter ---
		// load parameter
		parameter.getString("component", "name", connections.component.name);
		parameter.getString("component", "initialComponentMode", connections.component.initialComponentMode);
		if(parameter.checkIfParameterExists("component", "defaultScheduler")) {
			parameter.getString("component", "defaultScheduler", connections.component.defaultScheduler);
		}
		if(parameter.checkIfParameterExists("component", "useLogger")) {
			parameter.getBoolean("component", "useLogger", connections.component.useLogger);
		}
		
		// load parameters for client ColorQueryServiceReq
		parameter.getBoolean("ColorQueryServiceReq", "initialConnect", connections.colorQueryServiceReq.initialConnect);
		parameter.getString("ColorQueryServiceReq", "serviceName", connections.colorQueryServiceReq.serviceName);
		parameter.getString("ColorQueryServiceReq", "serverName", connections.colorQueryServiceReq.serverName);
		parameter.getString("ColorQueryServiceReq", "wiringName", connections.colorQueryServiceReq.wiringName);
		if(parameter.checkIfParameterExists("ColorQueryServiceReq", "roboticMiddleware")) {
			parameter.getString("ColorQueryServiceReq", "roboticMiddleware", connections.colorQueryServiceReq.roboticMiddleware);
		}
		// load parameters for client ObjectRecognitionQueryServiceReq
		parameter.getBoolean("ObjectRecognitionQueryServiceReq", "initialConnect", connections.objectRecognitionQueryServiceReq.initialConnect);
		parameter.getString("ObjectRecognitionQueryServiceReq", "serviceName", connections.objectRecognitionQueryServiceReq.serviceName);
		parameter.getString("ObjectRecognitionQueryServiceReq", "serverName", connections.objectRecognitionQueryServiceReq.serverName);
		parameter.getString("ObjectRecognitionQueryServiceReq", "wiringName", connections.objectRecognitionQueryServiceReq.wiringName);
		if(parameter.checkIfParameterExists("ObjectRecognitionQueryServiceReq", "roboticMiddleware")) {
			parameter.getString("ObjectRecognitionQueryServiceReq", "roboticMiddleware", connections.objectRecognitionQueryServiceReq.roboticMiddleware);
		}
		
		
		// load parameters for task Test
		parameter.getDouble("Test", "minActFreqHz", connections.test.minActFreq);
		parameter.getDouble("Test", "maxActFreqHz", connections.test.maxActFreq);
		parameter.getString("Test", "triggerType", connections.test.trigger);
		if(connections.test.trigger == "PeriodicTimer") {
			parameter.getDouble("Test", "periodicActFreqHz", connections.test.periodicActFreq);
		} else if(connections.test.trigger == "DataTriggered") {
			parameter.getString("Test", "inPortRef", connections.test.inPortRef);
			parameter.getInteger("Test", "prescale", connections.test.prescale);
		}
		if(parameter.checkIfParameterExists("Test", "scheduler")) {
			parameter.getString("Test", "scheduler", connections.test.scheduler);
		}
		if(parameter.checkIfParameterExists("Test", "priority")) {
			parameter.getInteger("Test", "priority", connections.test.priority);
		}
		if(parameter.checkIfParameterExists("Test", "cpuAffinity")) {
			parameter.getInteger("Test", "cpuAffinity", connections.test.cpuAffinity);
		}
		
		// load parameters for CTestROSExtension
		
		// load parameters for PlainOpcUaCTestExtension
		
		
		// load parameters for all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->loadParameters(parameter);
		}
		
	
	} catch (const SmartACE::IniParameterError & e) {
		std::cerr << e.what() << std::endl;
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std::exception: " << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}
